'use strict';

const {AMP_CACHE_HOST, appendRuntimeVersion} = require('./AmpConstants.js');

function calculateHost({
  ampUrlPrefix = AMP_CACHE_HOST,
  ampRuntimeVersion,
  lts = false,
  rtv = false,
}) {
  if (lts && rtv) {
    throw new Error('lts flag is not compatible with runtime version parameter');
